var midi=require('midi-js');

var device='/dev/snd/midiC2D3';

var midiin=new midi.input(device,function(code,msg){
	if (code) {
		console.log(msg)
		process.exit(code);
	} else {
		midiin.sysex(msg,sysex_callback);
	}
});

var midiout=new midi.output(device,function(code,msg) {
	if (code) {
		console.log(msg)
		process.exit(code);
	}
});

function sysex_callback(buffer) {
	var raw='';
	for (var i=0; i<buffer.length; ++i)
		raw+=buffer[i].toString(16)+' ';
	console.log(raw);
}

midiout.send([0xf0, 0xf7]);

